for(var toBuy=document.querySelectorAll(".to-buy"),modalCart=document.querySelector(".modal-cart"),closeModalCart=document.querySelector(".close-modal-cart"),continueBuying=document.querySelector(".modal-cart__button"),i=0;i<toBuy.length;i++)toBuy[i].addEventListener("click",function(e){e.preventDefault(),modalCart.classList.add("modal-show")});closeModalCart.addEventListener("click",function(e){e.preventDefault(),modalCart.classList.remove("modal-show")}),continueBuying.addEventListener("click",function(e){e.preventDefault(),modalCart.classList.remove("modal-show")}),window.addEventListener("keydown",function(e){27===e.keyCode&&modalCart.classList.contains("modal-show")&&(e.preventDefault(),modalCart.classList.remove("modal-show"))});